#!/usr/bin/env python import rospy from geometry_msgs.msg import Twist from sensor_msgs.msg import LaserScan
def scan_callback(msg): global g_range_ahead g_range_ahead = min(msg.ranges)
g_range_ahead = 1 # anything to start scan_sub = rospy.Subscriber('scan', LaserScan, scan_callback) cmd_vel_pub = rospy.Publisher('cmd_vel', Twist, queue_size=1) rospy.init_node('wander') state_change_time = rospy.Time.now() driving_forward = True rate = rospy.Rate(10)
while not rospy.is_shutdown(): if driving_forward: if (g_range_ahead < 0.8 or rospy.Time.now() > state_change_time): driving_forward = False state_change_time = rospy.Time.now() + rospy.Duration(5) else: # we're not driving_forward if rospy.Time.now() > state_change_time: driving_forward = True # we're done spinning, time to go forward! state_change_time = rospy.Time.now() + rospy.Duration(30) twist = Twist() if driving_forward: twist.linear.x = 1 else: twist.angular.z = 1 cmd_vel_pub.publish(twist)
rate.sleep()

if (g_range_ahead < 0.8 or rospy.Time.now() > state_change_time):
  driving_forward = False
  state_change_time = rospy.Time.now() + rospy.Duration(5)

if rospy.Time.now() > state_change_time:
  driving_forward = True # we're done spinning, time to go forward!
  state_change_time = rospy.Time.now() + rospy.Duration(30)

#!/usr/bin/env python import rospy from std_msgs.msg import String from geometry_msgs.msg import Twist
key_mapping = { 'w': [ 0, 1], 'x': [0, -1], 'a': [-1, 0], 'd': [1, 0], 's': [ 0, 0] }
def keys_cb(msg, twist_pub): if len(msg.data) == 0 or not key_mapping.has_key(msg.data[0]): return # unknown key vels = key_mapping[msg.data[0]] t = Twist() t.angular.z = vels[0] t.linear.x = vels[1] twist_pub.publish(t)
if __name__ == '__main__': rospy.init_node('keys_to_twist') twist_pub = rospy.Publisher('cmd_vel', Twist, queue_size=1) rospy.Subscriber('keys', String, keys_cb, twist_pub) rospy.spin()

if len(msg.data) == 0 or not key_mapping.has_key(msg.data[0]):
  return # unknown key
vels = key_mapping[msg.data[0]]

