def keys_cb(msg, twist_pub): global g_last_twist if len(msg.data) == 0 or not key_mapping.has_key(msg.data[0]): return # unknown key vels = key_mapping[msg.data[0]] g_last_twist.angular.z = vels[0] g_last_twist.linear.x = vels[1] twist_pub.publish(g_last_twist)
