class Follower: def __init__(self): self.bridge = cv_bridge.CvBridge() cv2.namedWindow("window", 1) self.image_sub = rospy.Subscriber('camera/rgb/image_raw', Image, self.image_callback) def image_callback(self, msg): image = self.bridge.imgmsg_to_cv2(msg,desired_encoding='bgr8') cv2.imshow("window", image) cv2.waitKey(3)
rospy.init_node('follower') follower = Follower() rospy.spin()

#!/usr/bin/env python import rospy, cv2, cv_bridge, numpy from sensor_msgs.msg import Image
class Follower: def __init__(self): self.bridge = cv_bridge.CvBridge() cv2.namedWindow("window", 1) self.image_sub = rospy.Subscriber('camera/rgb/image_raw', Image, self.image_callback) def image_callback(self, msg): image = self.bridge.imgmsg_to_cv2(msg) hsv = cv2.cvtColor(image, cv2.COLOR_BGR2HSV) lower_yellow = numpy.array([ 50, 50, 170]) upper_yellow = numpy.array([255, 255, 190]) mask = cv2.inRange(hsv, lower_yellow, upper_yellow) masked = cv2.bitwise_and(image, image, mask=mask) cv2.imshow("window", mask ) cv2.waitKey(3)
rospy.init_node('follower') follower = Follower() rospy.spin()

#!/usr/bin/env python

import rospy
from smach import State,StateMachine

from time import sleep


class One(State):
    def __init__(self):
        State.__init__(self, outcomes=['success'])

    def execute(self, userdata):
        print 'one'
        sleep(1)
        return 'success'

class Two(State):
    def __init__(self):
        State.__init__(self, outcomes=['success'])

    def execute(self, userdata):
        print 'two'
        sleep(1)
        return 'success'


if __name__ == '__main__':
    sm = StateMachine(outcomes=['success'])
    with sm:
        StateMachine.add('ONE', One(), transitions={'success':'TWO'})
        StateMachine.add('TWO', Two(), transitions={'success':'ONE'})

    sm.execute()

class One(State):
    def __init__(self):
        State.__init__(self, outcomes=['success'])

    def execute(self, userdata):
        print 'one'
        sleep(1)
        return 'success'

#!/usr/bin/env python
import rospy import actionlib from smach import State,StateMachine from move_base_msgs.msg import MoveBaseAction, MoveBaseGoal
waypoints = [ ['one', (2.1, 2.2), (0.0, 0.0, 0.0, 1.0)], ['two', (6.5, 4.43), (0.0, 0.0, -0.984047240305, 0.177907360295)] ]
class Waypoint(State): def __init__(self, position, orientation): State.__init__(self, outcomes=['success'])
# Get an action client self.client = actionlib.SimpleActionClient('move_base', MoveBaseAction) self.client.wait_for_server()
# Define the goal self.goal = MoveBaseGoal() self.goal.target_pose.header.frame_id = 'map' self.goal.target_pose.pose.position.x = position[0] self.goal.target_pose.pose.position.y = position[1] self.goal.target_pose.pose.position.z = 0.0 self.goal.target_pose.pose.orientation.x = orientation[0] self.goal.target_pose.pose.orientation.y = orientation[1] self.goal.target_pose.pose.orientation.z = orientation[2] self.goal.target_pose.pose.orientation.w = orientation[3]
def execute(self, userdata): self.client.send_goal(self.goal) self.client.wait_for_result() return 'success'
if __name__ == '__main__': rospy.init_node('patrol')
patrol = StateMachine('success') with patrol: for i,w in enumerate(waypoints): StateMachine.add(w[0], Waypoint(w[1], w[2]), transitions={'success':waypoints[(i + 1) % \ len(waypoints)][0]})
patrol.execute()

