from fake_sensor import FakeSensor
import rospy import tf
from geometry_msgs.msg import Quaternion
def make_quaternion(angle): q = tf.transformations.quaternion_from_euler(0, 0, angle) return Quaternion(*q)
def publish_value(value): angle = value * 2 * pi / 100.0 q = make_quaternion(angle) pub.publish(q)
if __name__ == '__main__': rospy.init_node('fake_sensor')
pub = rospy.Publisher('angle', Quaternion, queue_size=10)
sensor = FakeSensor() sensor.register_callback(publish_value)

def rotation_callback(goal):
    feedback = RotationFeedback()
    result = RotationResult()

    actuator.set_position(goal.orientation)
    success = True

    rate = rospy.Rate(10)
    while fabs(goal.orientation - actuator.position()) > 0.01:
        if a.is_preempt_requested():
            success = False
            break;

        feedback.current_orientation = actuator.position()
        a.publish_feedback(feedback)
        rate.sleep()

    result.final_orientation = actuator.position()
    if success:
        a.set_succeeded(result)
    else:
        a.set_preempted(result)

def loop(self):
    self.engine.startLoop(False)
    while not rospy.is_shutdown():
        self.engine.iterate()
        time.sleep(0.1)
    self.engine.endLoop()

#! /usr/bin/env python import rospy import threading, time, pyttsx import actionlib from basics.msg import TalkAction, TalkGoal, TalkResult
class TalkNode():
def __init__(self, node_name, action_name): rospy.init_node(node_name) self.server = actionlib.SimpleActionServer(action_name, TalkAction, self.do_talk, False) self.engine = pyttsx.init() self.engine_thread = threading.Thread(target=self.loop) self.engine_thread.start() self.engine.setProperty('volume', rospy.get_param('~volume', 1.0)) self.engine.setProperty('rate', rospy.get_param('~rate', 200.0)) self.preempt = rospy.get_param('~preempt', False) self.server.start()
def loop(self): self.engine.startLoop(False) while not rospy.is_shutdown(): self.engine.iterate() time.sleep(0.1) self.engine.endLoop()
def do_talk(self, goal): self.engine.say(goal.sentence) while self.engine.isBusy(): if self.preempt and self.server.is_preempt_requested(): self.engine.stop() while self.engine.isBusy(): time.sleep(0.1) self.server.set_preempted(TalkResult(), "Talk preempted") return time.sleep(0.1) self.server.set_succeeded(TalkResult(), "Talk completed successfully")
talker = TalkNode('speaker', 'speak') rospy.spin()

while self.engine.isBusy():
    if self.preempt and self.server.is_preempt_requested():
        self.engine.stop()
        while self.engine.isBusy():
            time.sleep(0.1)
        self.server.set_preempted(TalkResult(), "Talk preempted")
        return
    time.sleep(0.1)
self.server.set_succeeded(TalkResult(), "Talk completed successfully")

